window.addEventListener("load",()=>{document.querySelectorAll("[data-include]").forEach(e=>{const a=e.getAttribute("data-include");fetch(a).then(t=>{if(!t.ok)throw new Error(`Failed to load fragment: ${t.statusText}`);return t.text()}).then(t=>e.outerHTML=t).catch(t=>{console.error(t),e.innerHTML=t.message})})});
